package task8Package;

/*Write a program to print the characters from A to Z.*/

public class Q3Task8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char c;

		// Using For loop to print Alphabets in Capital from 'A' TO 'Z' using character
		// named c.
		for (c = 'A'; c <= 'Z'; c++) {
			System.out.print(c + " ");
		}

	}

}
